            UIView *infoButtonImageOverlay = [self GenerateSideBarInfoImageViewOverlay:infoButtonImage];
            
            
            
            
            [mainView addSubview:sectionLabel];
            [mainView addSubview:infoButtonImage];
            [mainView addSubview:infoButtonImageOverlay];
            
            
            
            
            return mainView;
            
